    function loadData() {
        $.ajax({
            url: '/JobPost/List',
            type: 'GET',
            contentType: 'application/json;charset=utf-8',
            dataType: 'JSON',
            success: function (result) {

                var pageCount = Math.ceil(result.length / pageLength);
                if (pageLayout < 1) {
                    pageLayout = 1;
                } else if (pageLayout > pageCount) {
                    pageLayout = pageCount;
                }
       ");
            WriteLiteral(@"         var startIndex = (pageLayout - 1) * pageLength;
                var endIndex = startIndex + pageLength;
                var currentTours = result.slice(startIndex, endIndex);

                var data = '';
                $.each(currentTours, function (key, item) {
                    data += '<tr>';
                    data += '<td>' + item.jobTypeName + '</p></td>';
                    const datetime = new Date(item.createdDate);
                    const year = datetime.getFullYear();
                    const month = datetime.getMonth() + 1;
                    const day = datetime.getDate();
                    const date = year + '-' + month + '-' + day;
                    data += '<td>' + date + '</p></td>';
                    data += '<td>' + item.location + '</p></td>';
                    data += '<td>' + item.status + '</p></td>';
                    data += '<td>' + item.salary + '</p></td>';
                    data += '<td>' + item.skill + '</p></td>';
             ");
