                    data += '<td><a href=""#"" onclick = ""return getById(' + item.id + ')"">Sửa</a> | <a href=""#"" onclick = ""return deletePost(' + item.id + ')"">Xóa</a></td>';
                    data += '</tr>';

                });
                $('.tbody').html(data);
                renderPagination(pageCount);
            },
            error: function (error) {
                console.log(""Error: "" + error.responseText);
            }
        });
    }
